@import url('https://fonts.googleapis.com/css2?family=PT+Sans:wght@400;700&family=Righteous&display=swap');

#alarm-view {
    width: 375px;
    height: 667px;
    margin: auto;
    background: #222222;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    
    button {
        appearance: none;
        opacity: 0.6;
        text-transform: uppercase;
        font-family: 'PT Sans', sans-serif;
        letter-spacing: 0.1em;
        border: 1px solid #FFFFFF;
        border-radius: 5px;
        background: none;
        color: white;
        padding: .4rem .4rem;
        font-size: 1rem;
    }


    section {
        width: 506px;
        height: 506px;
        display: flex;
        justify-content: center;
        align-items: center;
        flex-direction: column;
        background: rgba(255, 255, 255, 0.02);
        border-radius: 50%;
       

       section {
           width: 69%;
           height: 69%;

           section {
                width: 68%;
                height: 68%;

                h2 {
                    position: absolute;
                    margin-top: 200px;
                    font-family: 'PT Sans', sans-serif;
                    font-weight: bold;
                    color: white;
                    letter-spacing: 0.1em;
                    font-size: 36px;
                    line-height: 47px;
                    
                }

                section {
                    width: 65%;
                    height: 65%;
                }
           }
       }
    }

    &.hide {
        display: none;
    }

}
